mapper.select:
select t4.name as deptname,t3.* from (select t2.name as leadername ,t1.* from sys_user t1 left join sys_user t2 on(t1.mgr=t2.id) where t1.type!=0) t3 inner join sys_dept t4 on(t3.deptid=t4.id)
and t3.name like "%"#{name}"%"
and t3.address like "%"#{address}"%"
and t3.available =#{available}
and t3.deptid =#{deptid}
order by t3.ordernum
